import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';

// Import các màn hình
import HomeScreen from './screens/HomeScreen';
import CartScreen from './screens/CartScreen';

// Định nghĩa kiểu dữ liệu cho navigation stack
type RootStackParamList = {
  Home: undefined;
  CartScreen: { cart: Product[] };
};

const Stack = createStackNavigator<RootStackParamList>();

const App = () => {
  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen name="Home" component={HomeScreen} options={{ title: 'Sản phẩm' }} />
        <Stack.Screen name="CartScreen" component={CartScreen} options={{ title: 'Giỏ hàng của bạn' }} />
      </Stack.Navigator>
    </NavigationContainer>
  );
};

export default App;
